import { createRouter, createWebHistory } from 'vue-router';

// Імпортуємо компоненти з обох конфігурацій
import HomePage from './pages/HomePage.vue';
import LoginPage from './pages/LoginPage.vue';
import AdminPanel from './pages/AdminPanel.vue';
import AdminDashboard from './pages/AdminDashboard.vue';
import ManageProducts from './pages/ManageProducts.vue';
import ManageReviews from './pages/ManageReviews.vue';

// Створюємо спільний масив маршрутів
const routes = [
  // Маршрути з першої конфігурації
  { path: '/', component: HomePage },
  { path: '/login', component: LoginPage },
  {
    path: '/admin',
    component: AdminPanel,
    children: [
      {
        path: '',
        component: AdminDashboard,
        children: [
          { path: '', component: ManageProducts },
          { path: 'reviews', component: ManageReviews },
        ],
      },
      {
        path: 'dashboard',
        component: AdminDashboard,
        children: [
          { path: 'products', component: ManageProducts },
          { path: 'reviews', component: ManageReviews },
        ],
      },
    ],
  },
];

// Ініціалізуємо роутер
const router = createRouter({
  // Використовуємо єдиний `history` об'єкт
  history: createWebHistory('/my-shop7.9/'),
  routes,
});

export default router;
